package com.liveeasy.web.absorption.core.annotation;import java.lang.annotation.ElementType;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;/** * 供spi加载 * 用与作用与{@link com.liveeasy.web.absorption.core.pipeline.WebAbsorptionPipeline}和{@link com.liveeasy.web.absorption.core.processor.WebAbsorptionProcess} * 的实现类 */@Target(ElementType.TYPE)@Retention(RetentionPolicy.RUNTIME)public @interface SPI {    /**     * 爬虫名     * @return     */    String value();}