package com.liveeasy.web.absorption.core.factory;import java.util.List;/** * 克隆bean工厂 */public interface CloneBeanFactory {    /**     * 获取克隆对象     * @param target 目标对象的class对象     * @param <T>  对象类型     * @return 克隆对象     * @throws CloneNotSupportedException     */    <T extends Product> T getClone(Class<T> target) throws CloneNotSupportedException;    /**     * 获取克隆对象     * @param target 目标对象     * @param <T> 对象类型     * @return 克隆对象     */    <T extends Product> T getClone(T target);    /**     * 克隆对象集     * @param target 目标对象集     * @param <T> 对象类型     * @return 克隆对象集     */    <T extends Product> List<T> getCloneList(List<T> target);}