package com.liveeasy.web.absorption.core.factory;public abstract class AbstractFactoryImpl<T extends Product> implements AbstractFactory<T>{    protected T prototypeObject;    private Object lock = new Object();        @Override    public T getObject() throws CloneNotSupportedException {        if (null != prototypeObject){            return copy();        }        synchronized (lock){            if(prototypeObject == null){                prototypeObject = instance();            }        }        return copy();    }    protected abstract T copy() throws CloneNotSupportedException;    protected abstract T instance();}