package com.liveeasy.web.absorption.core.pipeline;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import tech.liveeasy.spider.webmagic.common.Product;import tech.liveeasy.spider.webmagic.pipeline.DataPipeline;import us.codecraft.webmagic.ResultItems;import us.codecraft.webmagic.Task;import us.codecraft.webmagic.pipeline.Pipeline;public  class WebMagicBasePipeline implements Pipeline , Product {    private DataPipeline dataPipeline;    private Logger logger = LoggerFactory.getLogger(WebMagicBasePipeline.class);    public void setDataPipeline(DataPipeline dataPipeline){        this.dataPipeline = dataPipeline;    }    @Override    public void process(ResultItems resultItems, Task task) {       dataPipeline.pipeline(resultItems , task);    }    @Override    public Product createClone() {        try {            return (Product) super.clone();        } catch (CloneNotSupportedException e) {            logger.error("createClone :" , e);        }        return null;    }}