package com.liveeasy.web.absorption.core.pipeline;import com.liveeasy.web.absorption.core.WebAbsorption;import com.liveeasy.web.absorption.core.factory.Product;import us.codecraft.webmagic.ResultItems;import us.codecraft.webmagic.Task;import java.util.List;/** * 数据管道接口 */public interface WebAbsorptionPipeline extends  Product{     /**      * 数据流管道类型      */     int DATA_STREAM_PIPELINE = 0;     /**      * 数据管道类型      */     int DATA_PIPELINE = 1;     /**      * 获取数据管道类型 可选{@link WebAbsorptionPipeline#DATA_PIPELINE}或者{@link WebAbsorptionPipeline#DATA_STREAM_PIPELINE}      * @return      */     int getType();     /**      * 每次获取的最后一个结果      * @param resultItem      * @param task      */     void lastPipeline(ResultItems resultItem, Task task);     /**      * 每次获取的结果集      * @param resultItems      * @param task      */     void pipeline(List<ResultItems> resultItems , Task task);}