package com.liveeasy.web.absorption.core.plugin;import us.codecraft.webmagic.proxy.Proxy;public class ProxyInfo {    private Long startTime;    private Long availability;    private Proxy proxy;    public ProxyInfo(Long startTime , Long availability , Proxy proxy){        this.startTime = startTime;        this.availability = availability;        this.proxy = proxy;    }    public Proxy getProxy(){       return this.proxy;    }    public boolean isInvalid(){        return System.currentTimeMillis() - startTime < (availability - 1000);    }}