package com.liveeasy.web.absorption.core.plugin;import us.codecraft.webmagic.Request;import us.codecraft.webmagic.Task;public abstract class SimpleDownloaderPlugin implements DownloaderPlugin{   protected Task task;    @Override    public void setTask(Task task) {        if(this.task != null){            return;        }        synchronized (this){            if(null == this.task){                this.task = task;            }        }    }    @Override    public void onSuccess(Request request) {        downloadPostPosition(request);    }    @Override    public void onError(Request request) {        downloadPostError(request);    }}