package com.liveeasy.web.absorption.core.processor;import com.liveeasy.web.absorption.core.processor.DataProcess;import us.codecraft.webmagic.Site;public abstract class AbstractDataProcessor implements DataProcess {    @Override    public DataProcess createClone() throws CloneNotSupportedException {        return (DataProcess) super.clone();    }    @Override    public Site getSite() {        return Site.me().setCycleRetryTimes(3).setSleepTime(100).setTimeOut(10000);    }}