package com.liveeasy.web.absorption.core.processor;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import tech.liveeasy.spider.webmagic.common.Product;import us.codecraft.webmagic.Page;import us.codecraft.webmagic.Site;import us.codecraft.webmagic.processor.PageProcessor;public  class WebMagicBasePageProcessor implements PageProcessor , Product {    private static Logger logger = LoggerFactory.getLogger(WebMagicBasePageProcessor.class);   private DataProcess dataProcess;    public WebMagicBasePageProcessor(){}    @Override    public void process(Page page) {        dataProcess.process(page);    }    @Override    public Site getSite() {        return dataProcess.getSite();    }    public void setDataProcess(DataProcess dataProcess){        this.dataProcess = dataProcess;    }    @Override    public Product createClone() throws CloneNotSupportedException {            return (Product) super.clone();    }}