package com.liveeasy.web.absorption.core.utils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import us.codecraft.webmagic.proxy.Proxy;import java.io.IOException;import java.net.InetSocketAddress;import java.net.Socket;/** *代理工具类 *@author */public class ProxyUtils {    private static Logger logger = LoggerFactory.getLogger(ProxyUtils.class);    public static boolean isAvailability(Proxy proxy , int timeout){        Socket socket = new Socket();        try {            socket.connect(new InetSocketAddress(proxy.getHost(), proxy.getPort()) , timeout);        } catch (IOException e) {            if(logger.isDebugEnabled()){                logger.debug("{}:{} not  availability" , proxy.getHost() , proxy.getPort());            }            return false;        } finally {            try {                socket.close();            } catch (IOException e) {                logger.error("socket close error :" , e);            }        }        return true;    }}