package com.liveeeasy.web.absorption.demo.spider.service;import com.liveeasy.web.absorption.core.WebAbsorption;import com.liveeeasy.web.absorption.demo.spider.ProxyDownloaderPlugin;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import redis.clients.jedis.JedisPool;import us.codecraft.webmagic.scheduler.RedisScheduler;@Servicepublic class SpiderService {        @Autowired        private JedisPool jedisPool;        public void start() throws IllegalAccessException {                WebAbsorption                        .newInstance()                        .setThreadNum(3)                        .setID("BeiKeEstate")                        .setDownloaderPlugin(new ProxyDownloaderPlugin())                        .setScheduler(new RedisScheduler(jedisPool))                        .builder("beiKeSpider" , "https://wh.ke.com/xiaoqu/")                        .runAsync();        }}