package com.liveeeasy.web.absorption.demo.spider.service;import com.liveeasy.web.absorption.core.WebAbsorption;import com.liveeasy.web.absorption.core.schehule.RedisDataResultItemsSchedule;import com.liveeeasy.web.absorption.demo.spider.HttpAPIService;import com.liveeeasy.web.absorption.demo.spider.ProxyDownloaderPlugin;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import redis.clients.jedis.JedisPool;import us.codecraft.webmagic.scheduler.RedisScheduler;@Servicepublic class SpiderService {        @Autowired        private JedisPool jedisPool;        @Autowired        private HttpAPIService httpAPIService;        public void start() throws IllegalAccessException {            ProxyDownloaderPlugin plugin = new ProxyDownloaderPlugin();            plugin.setJedisPool(jedisPool);            plugin.setHttpAPIService(httpAPIService);            plugin.setTestTimeout(1000);            plugin.setOrder("47fa8bed63f5f92185b6d7e000548749");            WebAbsorption                        .newInstance()                        .setThreadNum(3)                        .isAsyncPipeline(true)                        .setDataResultItemsSchedule(new RedisDataResultItemsSchedule(jedisPool))                        .setDataStreamSchedule(new RedisDataResultItemsSchedule(jedisPool))                        .setScheduler(new RedisScheduler(jedisPool))                        .setDownloaderPlugin(plugin)                        .builder("beiKeSpider" , "https://wh.ke.com/xiaoqu/")                        .run();        }}